sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;
	var userID = '/x/ndecd/demande-approvisionnement2/record/x_ndecd_demande_0_demande_d_approvisionnement/' + c.data.sys_idd;
	c.href = userID;
};

